import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [topics, setTopics] = useState([]);
    const [user, setUser] = useState(null);
    const [token, setToken] = useState(null);

    useEffect(() => {
        if (token) {
            const fetchTopics = async () => {
                const response = await axios.get('http://localhost:5000/topics', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setTopics(response.data);
            };
            fetchTopics();
        }
    }, [token]);

    const handleLogin = async (email, password) => {
        try {
            const response = await axios.post('http://localhost:5000/login', { email, password });
            setToken(response.data.token);
            alert('Login successful!');
        } catch (error) {
            alert('Login failed!');
        }
    };

    const handleCheckboxChange = async (subtopicId) => {
        try {
            await axios.patch(`http://localhost:5000/subtopics/${subtopicId}`, { userId: user }, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert('Marked as completed!');
        } catch (error) {
            alert('Failed to update progress!');
        }
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>DSA Sheet</h1>
                {token ? (
                    <div>
                        <h2>Topics</h2>
                        <ul>
                            {topics.map(topic => (
                                <li key={topic._id}>
                                    <h3>{topic.name} ({topic.level})</h3>
                                    <p>{topic.description}</p>
                                    <ul>
                                        {topic.subTopics.map(subtopic => (
                                            <li key={subtopic._id}>
                                                {subtopic.name}
                                                <input
                                                    type="checkbox"
                                                    onChange={() => handleCheckboxChange(subtopic._id)}
                                                />
                                            </li>
                                        ))}
                                    </ul>
                                </li>
                            ))}
                        </ul>
                    </div>
                ) : (
                    <div>
                        <h2>Login</h2>
                        <input type="email" placeholder="Email" id="email" />
                        <input type="password" placeholder="Password" id="password" />
                        <button onClick={() => handleLogin(
                            document.getElementById('email').value,
                            document.getElementById('password').value
                        )}>Login</button>
                    </div>
                )}
            </header>
        </div>
    );
}

export default App;
