import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
    const [topics, setTopics] = useState([]);
    const [user, setUser] = useState(null);
    const [token, setToken] = useState(localStorage.getItem('token') || null);

    useEffect(() => {
        if (token) {
            fetchTopics();
            fetchUser();
        }
    }, [token]);

    const fetchTopics = async () => {
        try {
            const response = await axios.get('http://localhost:5000/topics', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setTopics(response.data);
        } catch (error) {
            console.error('Error fetching topics:', error);
        }
    };

    const fetchUser = async () => {
        try {
            const response = await axios.get('http://localhost:5000/user', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setUser(response.data);
        } catch (error) {
            console.error('Error fetching user:', error);
        }
    };

    const handleLogin = async (email, password) => {
        try {
            const response = await axios.post('http://localhost:5000/users/login', { email, password });
            setToken(response.data.token);
            localStorage.setItem('token', response.data.token);
            alert('Login successful!');
        } catch (error) {
            console.error('Error during login:', error);
            alert('Login failed!');
        }
    };

    const handleLogout = () => {
        setToken(null);
        setUser(null);
        localStorage.removeItem('token');
        alert('Logged out successfully!');
    };

    const handleCheckboxChange = async (subtopicId) => {
        try {
            await axios.patch(
                `http://localhost:5000/subtopics/${subtopicId}`,
                { userId: user._id },
                {
                    headers: { Authorization: `Bearer ${token}` },
                }
            );
            alert('Marked as completed!');
            fetchTopics(); // Refresh topics to show updated state
        } catch (error) {
            console.error('Error updating progress:', error);
            alert('Failed to update progress!');
        }
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>DSA Sheet</h1>
                {token ? (
                    <div>
                        <button onClick={handleLogout}>Logout</button>
                        <h2>Topics</h2>
                        <ul>
                            {topics.map((topic) => (
                                <li key={topic._id}>
                                    <h3>
                                        {topic.name} ({topic.level})
                                    </h3>
                                    <p>{topic.description}</p>
                                    <h4>Subtopics:</h4>
                                    <ul>
                                        {topic.subTopics.map((subtopic) => (
                                            <li key={subtopic._id}>
                                                {subtopic.name}
                                                <input
                                                    type="checkbox"
                                                    checked={user?.completedSubtopics.includes(subtopic._id)}
                                                    onChange={() => handleCheckboxChange(subtopic._id)}
                                                />
                                            </li>
                                        ))}
                                    </ul>
                                </li>
                            ))}
                        </ul>
                    </div>
                ) : (
                    <div>
                        <h2>Login</h2>
                        <input type="email" placeholder="Email" id="email" />
                        <input type="password" placeholder="Password" id="password" />
                        <button
                            onClick={() =>
                                handleLogin(
                                    document.getElementById('email').value,
                                    document.getElementById('password').value
                                )
                            }
                        >
                            Login
                        </button>
                    </div>
                )}
            </header>
        </div>
    );
}

export default App;
