import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Topics = ({ token }) => {
    const [topics, setTopics] = useState([]);

    useEffect(() => {
        const fetchTopics = async () => {
            const response = await axios.get('http://localhost:5000/topics', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setTopics(response.data);
        };
        fetchTopics();
    }, [token]);

    const handleCheckboxChange = async (subtopicId) => {
        await axios.patch(`http://localhost:5000/topics/subtopics/${subtopicId}`, {}, {
            headers: { Authorization: `Bearer ${token}` },
        });
        alert('Marked as completed!');
    };

    return (
        <div>
            {topics.map((topic) => (
                <div key={topic._id}>
                    <h3>{topic.name} ({topic.level})</h3>
                    <p>{topic.description}</p>
                    <ul>
                        {topic.subTopics.map((subtopic) => (
                            <li key={subtopic._id}>
                                {subtopic.name}
                                <input type="checkbox" onChange={() => handleCheckboxChange(subtopic._id)} />
                            </li>
                        ))}
                    </ul>
                </div>
            ))}
        </div>
    );
};

export default Topics;
