import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [topics, setTopics] = useState([]);
    const [user, setUser] = useState(() => JSON.parse(localStorage.getItem('user')));
    const [token, setToken] = useState(() => localStorage.getItem('token'));

    useEffect(() => {
        if (token) {
            const fetchTopics = async () => {
                const response = await axios.get('http://localhost:5000/topics', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setTopics(response.data);
            };
            fetchTopics();
        }
    }, [token]);

    const handleLogin = async (email, password) => {
        try {
            const response = await axios.post('http://localhost:5000/login', { email, password });
            const { token, user } = response.data;
            localStorage.setItem('token', token);
            localStorage.setItem('user', JSON.stringify(user));
            setToken(token);
            setUser(user);
            alert('Login successful!');
        } catch (error) {
            alert('Login failed!');
        }
    };

    const handleCheckboxChange = async (subtopicId) => {
        try {
            const response = await axios.patch(
                `http://localhost:5000/subtopics/${subtopicId}`,
                { userId: user.id },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            setUser((prev) => ({
                ...prev,
                completedSubtopics: response.data.completedSubtopics,
            }));
            localStorage.setItem('user', JSON.stringify({
                ...user,
                completedSubtopics: response.data.completedSubtopics
            }));
            alert('Marked as completed!');
        } catch (error) {
            alert('Failed to update progress!');
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setToken(null);
        setUser(null);
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>DSA Sheet</h1>
                {token ? (
                    <div>
                        <button onClick={handleLogout}>Logout</button>
                        <h2>Topics</h2>
                        <ul>
                            {topics.map(topic => (
                                <li key={topic._id}>
                                    <h3>{topic.name} ({topic.level})</h3>
                                    <p>{topic.description}</p>
                                    <ul>
                                        {topic.subTopics.map(subtopic => (
                                            <li key={subtopic._id}>
                                                {subtopic.name}
                                                <input
                                                    type="checkbox"
                                                    checked={user?.completedSubtopics?.includes(subtopic._id)}
                                                    onChange={() => handleCheckboxChange(subtopic._id)}
                                                />
                                            </li>
                                        ))}
                                    </ul>
                                </li>
                            ))}
                        </ul>
                    </div>
                ) : (
                    <div>
                        <h2>Login</h2>
                        <input type="email" placeholder="Email" id="email" />
                        <input type="password" placeholder="Password" id="password" />
                        <button onClick={() => handleLogin(
                            document.getElementById('email').value,
                            document.getElementById('password').value
                        )}>Login</button>
                    </div>
                )}
            </header>
        </div>
    );
}

export default App;
