import React from "react";
import axios from "axios";

function SubtopicList({ subtopics, token }) {
    const handleCheckboxChange = async (subtopicId) => {
        try {
            await axios.patch(
                `http://localhost:5000/subtopics/${subtopicId}`,
                {},
                {
                    headers: { Authorization: `Bearer ${token}` },
                }
            );
            alert("Subtopic marked as completed!");
        } catch (error) {
            console.error("Failed to mark subtopic as completed:", error);
        }
    };

    return (
        <ul className="subtopic-list">
            {subtopics.map((subtopic) => (
                <li key={subtopic._id}>
                    <span>{subtopic.name}</span>
                    <input
                        type="checkbox"
                        onChange={() => handleCheckboxChange(subtopic._id)}
                    />
                    <ul>
                        {subtopic.youtubeLinks.map((link, index) => (
                            <li key={index}>
                                <a href={link} target="_blank" rel="noopener noreferrer">
                                    YouTube Tutorial
                                </a>
                            </li>
                        ))}
                        {subtopic.problemLinks.map((link, index) => (
                            <li key={index}>
                                <a href={link} target="_blank" rel="noopener noreferrer">
                                    Problem Link
                                </a>
                            </li>
                        ))}
                        {subtopic.articleLinks.map((link, index) => (
                            <li key={index}>
                                <a href={link} target="_blank" rel="noopener noreferrer">
                                    Article
                                </a>
                            </li>
                        ))}
                    </ul>
                </li>
            ))}
        </ul>
    );
}

export default SubtopicList;
